/**
* This class has been created to handle the various PeopleCode events for the Database
* Privileges page, e.g. UI, Errors/Warnings...
*
* @version 1.4
* @author  Andrew Casey
*/
class DatabasePrivs;
   method DatabasePrivs();
   method pageActivate();
   method btnClicked(&fButton As Field);
   method rcdChanged(&rData As Row);
   method resetTableName(&rData As Row);
   method saveLogic();
   rem Andrew Casey, 24/03/2017, Issue 255, Start;
   rem method disablePageLogic();
   method disableRowLogic(&rBuffer As Row);
   rem Andrew Casey, 24/03/2017, Issue 255, End;
   method errorIconLogic(&rRow As Row, &bShow As boolean);
   method privChkBoxLogic(&rRow As Row, &bEnable As boolean);
   rem Andrew Casey, 24/03/2017, Issue 255, Start;
   method arePrivsExistingAndCorrect(&rBuffer As Record) Returns boolean;
   method setPrivsIconLogic(&rBuffer As Row);
   method setPrivsIcon(&fProblemIcon As Field, &bProblem As boolean);
   method applyGrantsForObject(&rBuffer As Row);
   method deriveGrantsForObject(&rBuffer As Record) Returns string;
   method refreshThePage();
   method checkBoxClicked(&rBuffer As Row);
   method savePostChangeLogic(&rBuffer As Row);
   rem Andrew Casey, 24/03/2017, Issue 255, End;

   property Record rLvl0;
   property Record rLvl0Wrk;
   property Rowset rsLvl1;
   property string sLvl1Wrk;
   property string sDbOwner;
private;
   Constant &sTblPrefix = "PS_";
end-class;


/**
  * A constructor method which will be used to set various class specific properties.
  *
  */
method DatabasePrivs
   %This.rLvl0 = GetLevel0()(1).GetRecord(Record.BIS_DB_PRIV_TBL);
   %This.rLvl0Wrk = GetLevel0()(1).GetRecord(Record.BIS_DB_PRIV_WRK);
   %This.rsLvl1 = GetLevel0()(1).GetRowset(Scroll.BIS_DB_PRIV_TBL);
   %This.sLvl1Wrk = Record.BIS_DB_PRIV_WRK;
   Local string &sOwner;
   SQLExec(SQL.BIS_S_DBOWNER, %DbName, &sOwner);
   %This.sDbOwner = &sOwner;
end-method;

/**
  * A method to run when the page loads; set the various visibility attributes and
  * work with the grid columns (labels).
  *
  */
method pageActivate
   Local Grid &gDtls;

   rem If there is the first row, and there is no data (most likely in add mode) then
       hide the row;
   If %This.rsLvl1.ActiveRowCount = 1 And
         None(%This.rsLvl1.GetRow(1).BIS_DB_PRIV_TBL.SQLTABLENAME.Value) Then;
      %This.rsLvl1.GetRow(1).Visible = False;
   End-If;

   rem Andrew Casey, 24/03/2017, Issue 255, Start;
   rem %This.disablePageLogic();
   Local integer &i;
   For &i = 1 To %This.rsLvl1.ActiveRowCount;
      %This.disableRowLogic(%This.rsLvl1.GetRow(&i));
      %This.setPrivsIconLogic(%This.rsLvl1.GetRow(&i));
   End-For;
   rem Andrew Casey, 24/03/2017, Issue 255, End;

   rem blank out the column heading labels;
   &gDtls = GetGrid(Page.BIS_DB_PRIVS_TABLE, Record.BIS_DB_PRIV_TBL);
   &gDtls.ERROR_FLAG.Label = " ";
   &gDtls.EDIT_PB.Label = " ";
   rem Andrew Casey, 24/03/2017, Issue 255, Start;
   &gDtls.BIS_DB_GRANTS_FLG.Label = " ";
   rem Andrew Casey, 24/03/2017, Issue 255, End;
end-method;

/**
  * This method is used when a pusbutton has been clicked, e.g. to add a new row, or
  * edit a row.
  *
  * @param &fButton The field (button) being clicked.
  */
method btnClicked
   /+ &fButton as Field +/
   Local Field &fRecName, &fEditFlg, &fErrFlg;
   Evaluate &fButton.Name
   When Field.ADD_ROW
      rem check for default row, and make it available for use rather than inserting another
          row into the buffer;
      If %This.rsLvl1.ActiveRowCount = 1 And
            None(%This.rsLvl1.GetRow(1).BIS_DB_PRIV_TBL.SQLTABLENAME.Value) Then;
         %This.rsLvl1.GetRow(1).Visible = True;
         rem Andrew Casey, 23/03/2017, Issue 254, Start;
         rem &fRecName = %This.rsLvl1.GetRow(1).GetRecord(@("Record." | %This.sLvl1Wrk)).RECNAME;
         &fRecName = %This.rsLvl1.GetRow(1).GetRecord(@("Record." | %This.sLvl1Wrk)).SQLTABLENAME;
         rem Andrew Casey, 23/03/2017, Issue 254, End;
         &fRecName.DisplayOnly = False;
         %This.privChkBoxLogic(%This.rsLvl1.GetRow(1), True);
      Else;
         %This.rsLvl1.InsertRow(%This.rsLvl1.ActiveRowCount);
      End-If;
      &fEditFlg = %This.rsLvl1.GetRow(%This.rsLvl1.ActiveRowCount).GetRecord(@("Record." | %This.sLvl1Wrk)).EDIT_PB;
      &fErrFlg = %This.rsLvl1.GetRow(%This.rsLvl1.ActiveRowCount).GetRecord(@("Record." | %This.sLvl1Wrk)).ERROR_FLAG;

      %This.rsLvl1.GetRow(%This.rsLvl1.ActiveRowCount).BIS_DB_PRIV_TBL.SQLTABLENAME.Visible = False;
      %This.rsLvl1.GetRow(%This.rsLvl1.ActiveRowCount).BIS_DB_PRIV_TBL.BIS_DB_GRANTOR.Value = %This.sDbOwner;
      &fErrFlg.Visible = False;
      &fEditFlg.Visible = False;
      rem Andrew Casey, 30/06/2016, Start;
      rem Andrew Casey, 23/03/2017, Issue 254, Start;
      rem %This.rsLvl1.GetRow(%This.rsLvl1.ActiveRowCount).GetRecord(@("Record." | %This.sLvl1Wrk)).RECNAME.SetCursorPos(%Page);
      %This.rsLvl1.GetRow(%This.rsLvl1.ActiveRowCount).GetRecord(@("Record." | %This.sLvl1Wrk)).SQLTABLENAME.SetCursorPos(%Page);
      rem Andrew Casey, 23/03/2017, Issue 254, End;
      rem Andrew Casey, 30/06/2016, End;
      Break;
      rem Andrew Casey, 28/03/2017, Issue 255, Start;
   When Field.BIS_DB_GRANTS_FLG
      %This.applyGrantsForObject(GetRow());
      %This.refreshThePage();
      Break;
      rem Andrew Casey, 28/03/2017, Issue 255, End;
   When Field.EDIT_PB
      rem Enable the checkboxes, and allow the row to be deleted and disable the edit button;
      %This.privChkBoxLogic(GetRow(), True);
      GetRow().DeleteEnabled = True;
      GetField().Enabled = False;
   End-Evaluate;
end-method;

/**
  * This method is used to set the SQLTABLENAME value on the main table.
  * For example, MY_TABLE becomes PS_MY_TABLE.
  *
  * @param &rData  A row for which the record was changed.
  */
method rcdChanged
   /+ &rData as Row +/
   Local Field &fRecName, &fTblName;
   Local Record &rRecDefn = CreateRecord(Record.PSRECDEFN);

   rem Andrew Casey, 19/12/2016, Issue 248, Start;
   rem &fRecName = &rData.GetRecord(@("Record." | %This.sLvl1Wrk)).RECNAME;
   &fRecName = &rData.GetRecord(@("Record." | %This.sLvl1Wrk)).SQLTABLENAME;
   rem Andrew Casey, 19/12/2016, Issue 248, End;
   &fTblName = &rData.BIS_DB_PRIV_TBL.SQLTABLENAME;

   rem Derive the SQLTABLENAME from the PSRECDEFN table;
   If All(&fRecName.Value) Then;
      &rRecDefn.RECNAME.Value = &fRecName.Value;
      If &rRecDefn.SelectByKey() Then;
         If All(&rRecDefn.SQLTABLENAME.Value) Then;
            &fTblName.Value = &rRecDefn.SQLTABLENAME.Value;
         Else;
            &fTblName.Value = &sTblPrefix | &fRecName.Value;
         End-If;
      Else;
         rem Andrew Casey, 19/12/2016, Issue 248, Start;
         rem %This.resetTableName(&rData);
         &fTblName.Value = &fRecName.Value;
         rem Andrew Casey, 19/12/2016, Issue 248, End;
      End-If;
   Else;
      %This.resetTableName(&rData);
   End-If;

end-method;

/**
  * This method is used to set the SQLTABLENAME to a blank value.
  *
  * @param &rData  A row for which the record was changed.
  */
method resetTableName
   /+ &rData as Row +/
   &rData.BIS_DB_PRIV_TBL.SQLTABLENAME.Value = " ";
end-method;

/**
  * This method is used to perform various save logic:
  *   a) make sure that at least one privilege has been selected.
  *   b) make sure that the SQLTABLENAME has a value. 
  */
method saveLogic
   Local boolean &bErrNoPriv, &bErrNoTbl, &bErrLocal;
   Local integer &a;
   Local Field &fRecName, &fSelect, &fUpdate, &fDelete, &fInsert;

   For &a = 1 To %This.rsLvl1.ActiveRowCount;
      &bErrLocal = False;
      rem Andrew Casey, 19/12/2016, Issue 248, Start;
      rem &fRecName = %This.rsLvl1.GetRow(&a).GetRecord(@("Record." | %This.sLvl1Wrk)).RECNAME;
      &fRecName = %This.rsLvl1.GetRow(&a).GetRecord(@("Record." | %This.sLvl1Wrk)).SQLTABLENAME;
      rem Andrew Casey, 19/12/2016, Issue 248, End;
      &fSelect = %This.rsLvl1.GetRow(&a).BIS_DB_PRIV_TBL.SELECT_FLAG;
      &fUpdate = %This.rsLvl1.GetRow(&a).BIS_DB_PRIV_TBL.UPDATE_FLAG;
      &fDelete = %This.rsLvl1.GetRow(&a).BIS_DB_PRIV_TBL.DELETE_FLAG;
      &fInsert = %This.rsLvl1.GetRow(&a).BIS_DB_PRIV_TBL.INSERT_FLAG;

      rem Check each row that there is a least one privilege for the table;
      If &fSelect.Value = "0" And
            &fUpdate.Value = "0" And
            &fDelete.Value = "0" And
            &fInsert.Value = "0" Then;
         &bErrNoPriv = True;
         &bErrLocal = True;
      End-If;
      rem Check each row that there is a SQLTABLENAME value;
      If None(&fRecName.Value) Then;
         &bErrNoTbl = True;
         &bErrLocal = True;
      End-If;

      rem if there is a row in error then show the error flag;
      If &bErrLocal Then;
         %This.errorIconLogic(%This.rsLvl1.GetRow(&a), True);
      Else;
         %This.errorIconLogic(%This.rsLvl1.GetRow(&a), False);
      End-If;
      rem Andrew Casey, 24/03/2017, Issue 255, Start;
      %This.disableRowLogic(%This.rsLvl1.GetRow(&a));
      rem Andrew Casey, 24/03/2017, Issue 255, End;
   End-For;

   rem Trigger the error message;
   If &bErrNoPriv And
         Not &bErrNoTbl Then;
      Error MsgGet(28000, 12, "You must select at least one privilege.");
   Else;
      If &bErrNoTbl And
            Not &bErrNoPriv Then;
         Error MsgGet(28000, 11, "You must select a table, or delete the row.");
      Else;
         If &bErrNoTbl And
               &bErrNoPriv Then;
            Error MsgGet(28000, 13, "You must select at least one privilege, and you must select a record (or delete the row).");
         End-If;
      End-If;
   End-If;

   rem ... and the page display;
   rem Andrew Casey, 24/03/2017, Issue 255, Start;
   rem %This.disablePageLogic();
   rem Andrew Casey, 24/03/2017, Issue 255, End;
end-method;

/**
  * This method is used to perform various save logic:
  *   a) make sure that at least one privilege has been selected.
  *   b) make sure that the SQLTABLENAME has a value. 
  *
  * @param &rRow   The row for the field being changed.
  * @param &bShow  A boolean switched used to set the visibility.
  */
method errorIconLogic
   /+ &rRow as Row, +/
   /+ &bShow as Boolean +/
   Local Field &fErrFlg = &rRow.GetRecord(@("Record." | %This.sLvl1Wrk)).ERROR_FLAG;
   &fErrFlg.Visible = &bShow;
   &fErrFlg.Enabled = False;
end-method;

/**
  * This method will set the enabled property for the four privilege
  * checkboxes.
  *
  * @param &rRow     The row which is being affected.
  * @param &bEnable  A boolean switched used to set the enabled property.
  */
method privChkBoxLogic
   /+ &rRow as Row, +/
   /+ &bEnable as Boolean +/
   Local Field &fSelect, &fUpdate, &fDelete, &fInsert;
   &fSelect = &rRow.BIS_DB_PRIV_TBL.SELECT_FLAG;
   &fUpdate = &rRow.BIS_DB_PRIV_TBL.UPDATE_FLAG;
   &fDelete = &rRow.BIS_DB_PRIV_TBL.DELETE_FLAG;
   &fInsert = &rRow.BIS_DB_PRIV_TBL.INSERT_FLAG;
   &fSelect.Enabled = &bEnable;
   &fUpdate.Enabled = &bEnable;
   &fDelete.Enabled = &bEnable;
   &fInsert.Enabled = &bEnable;
end-method;

/**
  * This method is used to disable the various rows, records and fields on the page.
  *
  */
rem Andrew Casey, 24/03/2017, Issue 255, Start;
rem method disablePageLogic;
method disableRowLogic
   /+ &rBuffer as Row +/
   rem Andrew Casey, 24/03/2017, Issue 255, End;
   Local string &sTbl;
   Local integer &a;
   Local Field &fRecName, &fTblName, &fErrFlg, &fEditFlg;
   rem Andrew Casey, 24/03/2017, Issue 255, Start;
   rem For &a = 1 To %This.rsLvl1.ActiveRowCount;
   rem &fTblName = %This.rsLvl1.GetRow(&a).BIS_DB_PRIV_TBL.SQLTABLENAME;
   rem &fErrFlg = %This.rsLvl1.GetRow(&a).GetRecord(@("Record." | %This.sLvl1Wrk)).ERROR_FLAG;
   &fTblName = &rBuffer.BIS_DB_PRIV_TBL.SQLTABLENAME;
   &fErrFlg = &rBuffer.GetRecord(@("Record." | %This.sLvl1Wrk)).ERROR_FLAG;
   rem Andrew Casey, 24/03/2017, Issue 255, End;
   rem Andrew Casey, 19/12/2016, Issue 248, Start;
   rem &fRecName = %This.rsLvl1.GetRow(&a).GetRecord(@("Record." | %This.sLvl1Wrk)).RECNAME;
   rem Andrew Casey, 24/03/2017, Issue 255, Start;
   rem &fRecName = %This.rsLvl1.GetRow(&a).GetRecord(@("Record." | %This.sLvl1Wrk)).SQLTABLENAME;
   &fRecName = &rBuffer.GetRecord(@("Record." | %This.sLvl1Wrk)).SQLTABLENAME;
   rem Andrew Casey, 24/03/2017, Issue 255, End;
   rem Andrew Casey, 19/12/2016, Issue 248, End;
   rem Andrew Casey, 24/03/2017, Issue 255, Start;
   rem &fEditFlg = %This.rsLvl1.GetRow(&a).GetRecord(@("Record." | %This.sLvl1Wrk)).EDIT_PB;
   &fEditFlg = &rBuffer.GetRecord(@("Record." | %This.sLvl1Wrk)).EDIT_PB;
   rem Andrew Casey, 24/03/2017, Issue 255, End;
   &sTbl = &fTblName.Value;
   SQLExec("SELECT RECNAME FROM PSRECDEFN A WHERE 1=1 AND A.RECNAME=:1", &sTbl, &fRecName.Value);
   If None(&fRecName.Value) Then;
      SQLExec("SELECT RECNAME FROM PSRECDEFN A WHERE 1=1 AND A.RECNAME=:1", Substring(&sTbl, 4, Len(&sTbl) - 3), &fRecName.Value);
   End-If;
   rem Andrew Casey, 19/12/2016, Issue 248, Start;
   If None(&fRecName.Value) Then;
      &fRecName.Value = &fTblName.Value;
   End-If;
   rem Andrew Casey, 19/12/2016, Issue 248, End;
   &fTblName.Visible = False;
   &fRecName.DisplayOnly = True;
   &fErrFlg.Visible = False;
   &fEditFlg.Visible = True;

   rem Andrew Casey, 24/03/2017, Issue 255, Start;
   rem %This.privChkBoxLogic(%This.rsLvl1.GetRow(&a), False);
   rem %This.rsLvl1.GetRow(&a).DeleteEnabled = False;
   rem End-For;
   &fEditFlg.Enabled = True;
   %This.privChkBoxLogic(&rBuffer, False);
   &rBuffer.DeleteEnabled = False;
   rem Andrew Casey, 24/03/2017, Issue 255, End;

end-method;

rem Andrew Casey, 24/03/2017, Issue 255, Start;
method arePrivsExistingAndCorrect
   /+ &rBuffer as Record +/
   /+ Returns Boolean +/
   Local Rowset &rDatabasePrivs;
   Local Record &rDatabasePriv;
   Local integer &i;

   &rDatabasePrivs = GetLevel0()(1).GetRowset(Scroll.BIS_USR_PRIV_VW);
   For &i = 1 To &rDatabasePrivs.ActiveRowCount;
      &rDatabasePriv = &rDatabasePrivs(&i).BIS_USR_PRIV_VW;
      If &rDatabasePriv.SQLTABLENAME.Value = &rBuffer.SQLTABLENAME.Value Then;
         Return ( Not &rDatabasePriv.CompareFields(&rBuffer));
      End-If;
   End-For;

   Return True;
end-method;

method setPrivsIconLogic
   /+ &rBuffer as Row +/
   %This.setPrivsIcon(&rBuffer.GetRecord(@("Record." | %This.sLvl1Wrk)).BIS_DB_GRANTS_FLG, %This.arePrivsExistingAndCorrect(&rBuffer.BIS_DB_PRIV_TBL));
end-method;

method setPrivsIcon
   /+ &fProblemIcon as Field, +/
   /+ &bProblem as Boolean +/
   If &bProblem Then;
      &fProblemIcon.LabelImage = Image.PS_RED_CATEGORY_16_ICN;
      &fProblemIcon.Enabled = True;
   Else;
      &fProblemIcon.LabelImage = Image.PS_GREEN_CATEGORY_16_ICN;
      &fProblemIcon.Enabled = False;
   End-If;
end-method;

method applyGrantsForObject
   /+ &rBuffer as Row +/
   If All(&rBuffer.BIS_DB_PRIV_TBL.SQLTABLENAME.Value) And
         (&rBuffer.BIS_DB_PRIV_TBL.SELECT_FLAG.Value = 1 Or
            &rBuffer.BIS_DB_PRIV_TBL.UPDATE_FLAG.Value = 1 Or
            &rBuffer.BIS_DB_PRIV_TBL.DELETE_FLAG.Value = 1 Or
            &rBuffer.BIS_DB_PRIV_TBL.INSERT_FLAG.Value = 1) Then;
      rem REVOKE ALL ON PS_BIS_TREE_1 FROM BGL_RPTG_TABLEAU;
      SQLExec("REVOKE ALL ON " | &rBuffer.BIS_DB_PRIV_TBL.SQLTABLENAME.Value | " FROM " | &rBuffer.BIS_DB_PRIV_TBL.BIS_DB_GRANTEE.Value);
      rem GRANT SELECT ON PS_BIS_TREE_1 FROM BGL_RPTG_TABLEAU;
      SQLExec("GRANT " | %This.deriveGrantsForObject(&rBuffer.BIS_DB_PRIV_TBL) | " ON " | &rBuffer.BIS_DB_PRIV_TBL.SQLTABLENAME.Value | " TO " | &rBuffer.BIS_DB_PRIV_TBL.BIS_DB_GRANTEE.Value);
   End-If;
end-method;

method deriveGrantsForObject
   /+ &rBuffer as Record +/
   /+ Returns String +/
   Local string &sGrantStmt;

   If &rBuffer.SELECT_FLAG.Value = 1 Then;
      &sGrantStmt = "SELECT, ";
   End-If;
   If &rBuffer.UPDATE_FLAG.Value = 1 Then;
      &sGrantStmt = &sGrantStmt | "UPDATE, ";
   End-If;
   If &rBuffer.DELETE_FLAG.Value = 1 Then;
      &sGrantStmt = &sGrantStmt | "DELETE, ";
   End-If;
   If &rBuffer.INSERT_FLAG.Value = 1 Then;
      &sGrantStmt = &sGrantStmt | "INSERT";
   End-If;


   Return RTrim(&sGrantStmt, ", ");

end-method;

method refreshThePage
   Local Record &rSrchRcd = CreateRecord(Record.BIS_DB_PRV_SRCH);
   &rSrchRcd.BIS_DB_GRANTEE.Value = %This.rLvl0.BIS_DB_GRANTEE.Value;
   TransferExact( False, MenuName.BIS_BGL_MENU, BarName.SETUP, ItemName.BIS_DB_PRIVS_CMP, Page.BIS_DB_PRIVS_TABLE, %Action_UpdateDisplay, &rSrchRcd);
end-method;

method checkBoxClicked
   /+ &rBuffer as Row +/
   %This.setPrivsIconLogic(&rBuffer);
end-method;

method savePostChangeLogic
   /+ &rBuffer as Row +/

   If &rBuffer.IsDeleted Then;
      rem REVOKE ALL ON PS_BIS_TREE_1 FROM BGL_RPTG_TABLEAU;
      SQLExec("REVOKE ALL ON " | &rBuffer.BIS_DB_PRIV_TBL.SQLTABLENAME.Value | " FROM " | &rBuffer.BIS_DB_PRIV_TBL.BIS_DB_GRANTEE.Value);
   End-If;
end-method;

